CREATE OR REPLACE FUNCTION obter_se_possui_pac_ale (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_alergia_w    bigint:=0;
ds_expressao_w  varchar(255):='';


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '')then
	select	count(*)
	into STRICT	ie_alergia_w
	from    paciente_alergia
	where   cd_pessoa_fisica  = cd_pessoa_fisica_p;
end if;

if (ie_alergia_w > 0)then
	ds_expressao_w :=	obter_desc_expressao(883834);
end if;

return	ds_expressao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
