CREATE OR REPLACE FUNCTION obter_se_possui_prescr_opme ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



ie_resultado_w	varchar(1) := 'N';
quantidade_w	bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select count(*)
	into STRICT   quantidade_w
	from prescr_opme
	where nr_prescricao = nr_prescricao_p
	and	coalesce(ie_suspenso, 'N')	<> 'S';

	if (quantidade_w > 0) then
	   ie_resultado_w := 'S';
	end if;

	select count(*)
	into STRICT   quantidade_w
	from prescr_opm
	where nr_prescricao = nr_prescricao_p;

	if (quantidade_w > 0) then
	   ie_resultado_w := 'S';
	end if;

	select count(*)
	into STRICT quantidade_w
	from PRESCR_PROTESE_MS
	where nr_prescricao = nr_prescricao_p
	and	coalesce(ie_suspenso, 'N')	<> 'S';

	if (quantidade_w > 0) then
	   ie_resultado_w := 'S';
	end if;

	select count(*)
	into STRICT   quantidade_w
	from   PRESCR_PROTESE_MI
	where  nr_prescricao = nr_prescricao_p
	and    coalesce(ie_suspenso, 'N')	<> 'S';

	if (quantidade_w > 0) then
	   ie_resultado_w := 'S';
	end if;

	select count(*)
	into STRICT   quantidade_w
	from   PRESCR_ORTESE_MS
	where  nr_prescricao = nr_prescricao_p
	and    coalesce(ie_suspenso, 'N')	<> 'S';

	if (quantidade_w > 0) then
	   ie_resultado_w := 'S';
	end if;

	select count(*)
	into STRICT   quantidade_w
	from   PRESCR_ORTESE_MI
	where  nr_prescricao = nr_prescricao_p
	and    coalesce(ie_suspenso, 'N')	<> 'S';

	if (quantidade_w > 0) then
	   ie_resultado_w := 'S';
	end if;

	select count(*)
	into STRICT   quantidade_w
	from   PRESCR_MEIO_LOCOMOCAO
	where  nr_prescricao = nr_prescricao_p
	and    coalesce(ie_suspenso, 'N')	<> 'S';

	if (quantidade_w > 0) then
	   ie_resultado_w := 'S';
	end if;

	select count(*)
	into STRICT   quantidade_w
	from   prescr_compl_opm
	where  nr_prescricao = nr_prescricao_p
	and    coalesce(ie_suspenso, 'N')	<> 'S';

	if (quantidade_w > 0) then
	   ie_resultado_w := 'S';
	end if;

	select count(*)
	into STRICT   quantidade_w
	from   PRESCR_ORTESE_COLUNA
	where  nr_prescricao = nr_prescricao_p
	and    coalesce(ie_suspenso, 'N')	<> 'S';

	if (quantidade_w > 0) then
	   ie_resultado_w := 'S';
	end if;

end if;

return	ie_resultado_w;

end;
