SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_possui_prescr (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_possui_registros_w	varchar(1);
dt_referencia_w			timestamp	:= clock_timestamp() - interval '2 days';


BEGIN

select	coalesce(max('S'),'N') ie_possui_registros
into STRICT	ie_possui_registros_w
from 	prescr_material b,
		prescr_medica a
where	a.nr_prescricao	= b.nr_prescricao
and		a.nr_atendimento	= nr_atendimento_p
and		a.dt_prescricao		between dt_referencia_w and clock_timestamp() + interval '2 days'
and		(coalesce(a.dt_liberacao, a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao, a.dt_liberacao_medico))::text <> '')
and		b.ie_suspenso		= 'N';

if (ie_possui_registros_w = 'N') then
	select	coalesce(max('S'),'N') ie_possui_registros
	into STRICT	ie_possui_registros_w
	from	prescr_procedimento b,
			prescr_medica a
	where	a.nr_prescricao		= b.nr_prescricao
	and		coalesce(b.nr_seq_origem::text, '') = ''
	and		a.nr_atendimento	= nr_atendimento_p
	and		(coalesce(a.dt_liberacao, a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao, a.dt_liberacao_medico))::text <> '')
	and		b.ie_suspenso		= 'N';
end if;

return	ie_possui_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
