SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_possui_processo (nr_seq_nota_p bigint, nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(1);
qtd_w           bigint;


BEGIN

if (ie_opcao_p = '2010') then

	select count(*)
	into STRICT   qtd_w
	from   fis_reinf_r1070 a,
	       fis_reinf_r2010 b,
	       nota_fiscal c
	where  c.nr_sequencia = nr_seq_nota_p
	and    b.nr_sequencia = nr_sequencia_p
	and    b.cd_tributo = a.cd_tributo
	and    c.cd_cgc_emitente = a.cd_cnpj
	and    to_date(b.dt_competencia,'dd/mm/yyyy') between to_date(a.dt_inicio_validade,'dd/mm/yyyy') and fim_dia(to_date(coalesce(a.dt_fim_validade, clock_timestamp()),'dd/mm/yyyy'));

else

	select count(*)
	into STRICT   qtd_w
	from   fis_reinf_r1070 a,
	       fis_reinf_r2020 b,
	       nota_fiscal c
	where  c.nr_sequencia = nr_seq_nota_p
	and    b.nr_sequencia = nr_sequencia_p
	and    b.cd_tributo = a.cd_tributo
	and    c.cd_cgc = a.cd_cnpj
	and    to_date(b.dt_competencia,'dd/mm/yyyy') between to_date(a.dt_inicio_validade,'dd/mm/yyyy') and fim_dia(to_date(coalesce(a.dt_fim_validade, clock_timestamp()),'dd/mm/yyyy'));

end if;


if (qtd_w > 0) then

ds_retorno_w := 'S';

else

ds_retorno_w := 'N';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
