CREATE OR REPLACE FUNCTION obter_se_possui_registro_kv (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_possui_KV	varchar(1);
ds_retorno_KV	varchar(255);


BEGIN

select	coalesce(max('S'),'N') possui_muster_1
into STRICT	ie_possui_KV
from	atestado_paciente
where	nr_atendimento = nr_atendimento_p
and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and		coalesce(dt_inativacao::text, '') = '';

if (ie_possui_KV = 'S') then
	if (coalesce(ds_retorno_KV::text, '') = '') then
		ds_retorno_KV := Wheb_mensagem_pck.get_texto(1033783);
	else
		ds_retorno_KV := ds_retorno_KV||', '||Wheb_mensagem_pck.get_texto(1033783);
	end if;
end if;

select	coalesce(max('S'),'N') possui_muster_16
into STRICT	ie_possui_KV
from	kv_receita_farmacia
where	nr_atendimento = nr_atendimento_p
and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and		coalesce(dt_inativacao::text, '') = '';

if (ie_possui_KV = 'S') then
	if (coalesce(ds_retorno_KV::text, '') = '') then
		ds_retorno_KV := Wheb_mensagem_pck.get_texto(1033785);
	else
		ds_retorno_KV := ds_retorno_KV||', '||Wheb_mensagem_pck.get_texto(1033785);
	end if;
end if;

return	ds_retorno_KV;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
