CREATE OR REPLACE FUNCTION obter_se_possui_regra_gedipa (cd_material_p bigint, cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_resultado_w	varchar(1) := 'N';
qtd_w			bigint;


BEGIN

	select	count(*)
	into STRICT	qtd_w
	from 	regra_lib_hor_medic_rep x
	where   ((x.cd_material = cd_material_p)
	or (x.cd_grupo_material  = cd_grupo_material_p)
	or (x.cd_subgrupo_material = cd_subgrupo_material_p)
	or (x.cd_classe_material  = cd_classe_material_p))
	and 	x.ie_situacao = 'A';

	if (qtd_w > 0) then
		ie_resultado_w := 'S';
	end if;

	return	ie_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
