CREATE OR REPLACE FUNCTION obter_se_possui_regra_img ( nr_seq_laudo_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_tipo_visualizacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';


BEGIN
-- verificar se possui alguma regra cadastrada.
select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ds_retorno_w
from	regra_visualizacao_imagem;

if (ds_retorno_w = 'N') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	procedimento_paciente b,
		procedimento c,
		regra_visualizacao_imagem a
	where	b.cd_procedimento = c.cd_procedimento
	and	b.ie_origem_proced = c.ie_origem_proced
	and	c.cd_tipo_procedimento =  a.cd_tipo_procedimento
	and	coalesce(ie_tipo_visualizacao,'E') = ie_tipo_visualizacao_p
	and	((b.nr_laudo = nr_seq_laudo_p)
	or (b.nr_prescricao = nr_prescricao_p
	and (b.nr_sequencia_prescricao = nr_seq_prescr_p or nr_seq_prescr_p = 0)));

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
