CREATE OR REPLACE FUNCTION obter_se_possui_saldo_req (nr_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_req_possui_saldo_w	varchar(1) := 'N';

qt_requisitada_w	double precision;
qt_saldo_w			double precision;
nr_sequencia_w		integer;
qt_material_req_saldo_w	integer;

C01 CURSOR FOR
SELECT	nr_sequencia,
	qt_material_requisitada
from	item_requisicao_material
where	cd_motivo_baixa = 0
and		nr_requisicao = nr_requisicao_p;


BEGIN

ie_req_possui_saldo_w	:= 'N';

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	qt_requisitada_w;
exit when(C01%notfound or (ie_req_possui_saldo_w = 'S'));
	begin
	qt_saldo_w := obter_saldo_estoque_requisicao(nr_requisicao_p, nr_sequencia_w);

	if (qt_saldo_w >= qt_requisitada_w) then
		ie_req_possui_saldo_w := 'S';
	end if;
	end;
end loop;
close C01;

return	ie_req_possui_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
