CREATE OR REPLACE FUNCTION obter_se_possui_servico_sne (nr_prescricao_p bigint, nr_atendimento_p bigint, dt_horario_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
nr_seq_servico_w	bigint;


BEGIN
Select	Max(a.nr_sequencia), CASE WHEN coalesce(max(a.dt_liberacao)::text, '') = '' THEN  'Y'  ELSE 'N' END
into STRICT 	nr_seq_servico_w,
	ie_retorno_w
from	NUT_SERVICO b,
	NUT_ATEND_SERV_DIA a
where	nr_atendimento	= nr_atendimento_p
and	b.nr_sequencia	= a.NR_SEQ_SERVICO
and	a.DT_SERVICO	= dt_horario_p;

if (nr_seq_servico_w IS NOT NULL AND nr_seq_servico_w::text <> '') then
	Select	coalesce(MAX('N'),'Y')
	into STRICT	ie_retorno_w
	from 	NUT_ATEND_SERV_DIA_REP
	where	NR_PRESCR_ENTERAL	= nr_prescricao_p
	and	NR_SEQ_SERV_DIA		= nr_seq_servico_w;
else
	ie_retorno_w := 'N';
end if;

return	ie_retorno_w;

end;
$body$
