CREATE OR REPLACE FUNCTION obter_se_possui_vagas_mutirao (nr_seq_mutirao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(1);				
				

BEGIN

if (nr_seq_mutirao_p IS NOT NULL AND nr_seq_mutirao_p::text <> '')then
	select	coalesce(max('S'), 'N')  qtd
	into STRICT	ds_retorno_w
	from	mutirao_lista_espera a
	where 	a.nr_sequencia = nr_seq_mutirao_p
	and     trunc(coalesce(a.dt_termino, clock_timestamp())) > trunc(clock_timestamp())
