CREATE OR REPLACE FUNCTION obter_se_possui_vaga (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_retorno_w		smallint;
ie_dt_futura_w		varchar(1);
ds_status_solic_w	varchar(255);
			

BEGIN 
 
ie_dt_futura_w := Obter_param_Usuario(916, 1169, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_dt_futura_w);
ds_status_solic_w := Obter_param_Usuario(916, 1170, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ds_status_solic_w);
 
if (ds_status_solic_w IS NOT NULL AND ds_status_solic_w::text <> '') then 
	ds_status_solic_w := replace(ds_status_solic_w, ' ', '');
	if (position(',' in ds_status_solic_w) = 0) then 
		ds_status_solic_w := ds_status_solic_w || ',';
	end if;
end if;
 
if (ie_dt_futura_w = 'S') then 
	if (coalesce(ds_status_solic_w::text, '') = '') then 
		SELECT coalesce(max(1),0) qt_vagas 
		into STRICT	qt_retorno_w   
		FROM  GESTAO_VAGA 
		WHERE  nr_atendimento = nr_atendimento_p 
		and	dt_prevista > clock_timestamp();
	else 
		SELECT coalesce(max(1),0) qt_vagas 
		into STRICT	qt_retorno_w   
		FROM  GESTAO_VAGA 
		WHERE  nr_atendimento = nr_atendimento_p 
		and	dt_prevista > clock_timestamp()	 
		and	obter_se_contido_entre_virgula(ds_status_solic_w, IE_STATUS) = 'S';
	end if;
else 
	if (coalesce(ds_status_solic_w::text, '') = '') then 
		SELECT coalesce(max(1),0) qt_vagas 
		into STRICT	qt_retorno_w   
		FROM  GESTAO_VAGA 
		WHERE  nr_atendimento = nr_atendimento_p;
	else 
		SELECT coalesce(max(1),0) qt_vagas 
		into STRICT	qt_retorno_w   
		FROM  GESTAO_VAGA 
		WHERE  nr_atendimento = nr_atendimento_p 
		and	obter_se_contido_entre_virgula(ds_status_solic_w, IE_STATUS) = 'S';
	end if;
end if;
 
 
return	qt_retorno_w;
 
end;
$body$
