CREATE OR REPLACE FUNCTION obter_se_precaucao_lib (nr_seq_precaucao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w	bigint;


BEGIN

if (nr_seq_precaucao_p IS NOT NULL AND nr_seq_precaucao_p::text <> '') then
	select	count(*)
	into STRICT	qt_reg_w
	from	cih_precaucao_lib
	where	nr_seq_precaucao = nr_seq_precaucao_p;

	if (qt_reg_w > 0) then
		select	count(*)
		into STRICT	qt_reg_w
		from	cih_precaucao_lib
		where	nr_seq_precaucao = nr_seq_precaucao_p
		and	cd_perfil = obter_perfil_ativo;

		if (qt_reg_w = 0) then
			return 'N';
		end if;
	end if;
end if;

return	'S';

end;
$body$
LANGUAGE PLPGSQL
