SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_preceptor_lib (cd_profissional_p text, cd_pessoa_estudante_p text, nr_seq_item_pront_p bigint, cd_avaliador_aux_p bigint default 0) RETURNS varchar AS $body$
DECLARE


qt_reg_w 			bigint;
ie_mostrar_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);


BEGIN

ie_mostrar_w := 'N';

if (cd_avaliador_aux_p > 0) and (cd_avaliador_aux_p <> cd_pessoa_estudante_p) then
	
	if (cd_profissional_p = cd_pessoa_estudante_p) then
		ie_mostrar_w := 'S';
	else
    select	count(1)
		into STRICT	qt_reg_w
		from	pessoa_fisica_estudante a,
      liberacao_item_estudante b,
      pessoa_fisica_preceptor c
		where	a.cd_pessoa_fisica = cd_avaliador_aux_p
		and		a.cd_pessoa_fisica = b.cd_pessoa_estudante
    and   a.cd_pessoa_fisica = c.cd_pessoa_estudante
    and   c.cd_pessoa_fisica = cd_profissional_p
		and		clock_timestamp() between coalesce(dt_inicio,clock_timestamp()) and coalesce(dt_fim,clock_timestamp())
		and		b.nr_seq_item = nr_seq_item_pront_p
    and		COALESCE(b.cd_perfil, obter_perfil_ativo, 0)	= coalesce(obter_perfil_ativo,0)
		and	a.IE_SITUACAO = 'A';
    if (qt_reg_w > 0) then
      ie_mostrar_w := 'S';
		else
      ie_mostrar_w := 'N';
    end if;
	end if;
	
else

	if (get_if_rule_preceptor(cd_pessoa_estudante_p) = 'S') then
	
		select	count(1)
		into STRICT	qt_reg_w
		from	pessoa_fisica_estudante a, liberacao_item_estudante b
		where	cd_pessoa_fisica = cd_pessoa_estudante_p
		and		a.cd_pessoa_fisica = b.cd_pessoa_estudante
		and		clock_timestamp() between coalesce(dt_inicio,clock_timestamp()) and coalesce(dt_fim,clock_timestamp())
		and		b.nr_seq_item = nr_seq_item_pront_p
		and		coalesce(b.cd_perfil, coalesce(obter_perfil_ativo,0))	= coalesce(obter_perfil_ativo,0)
		and	a.IE_SITUACAO = 'A';
		
		if (qt_reg_w > 0) then
		
			select	coalesce(max('S'),'N')
			into STRICT	ie_mostrar_w
			from 	pessoa_fisica_preceptor
			where (cd_pessoa_fisica = cd_profissional_p
			and		cd_pessoa_estudante = cd_pessoa_estudante_p
			and (cd_perfil = coalesce(obter_perfil_ativo,0) or coalesce(cd_perfil::text, '') = ''))
			or (coalesce(cd_pessoa_fisica::text, '') = ''
			and 	cd_perfil = coalesce(obter_perfil_ativo,0)
			and		cd_pessoa_estudante = cd_pessoa_estudante_p);
			
		end if;
	else
	
		select	coalesce(max('S'),'N')
		into STRICT 	ie_mostrar_w
		from	regra_preceptor_pep a, regra_preceptor_pep_item b, regra_preceptor_pep_prof c
		where	a.nr_sequencia = b.nr_seq_regra
		and		a.nr_sequencia = c.nr_seq_regra
		and 	a.cd_perfil = coalesce(obter_perfil_ativo,0)
		and		c.cd_pessoa_fisica = cd_profissional_p
		and		b.nr_seq_item = nr_seq_item_pront_p
		and		clock_timestamp() between coalesce(a.dt_inicio,clock_timestamp()) and coalesce(a.dt_fim,clock_timestamp())
		and		coalesce(a.ie_situacao,'A') = 'A';
		
	end if;

end if;

return ie_mostrar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
