CREATE OR REPLACE FUNCTION obter_se_pref_lib_agenda (nr_seq_preferencia_p bigint, cd_agenda_p bigint, cd_perfil_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
qt_regra_usu_w	bigint;
nr_seq_regra_w	bigint;
qt_regra_w	bigint;


BEGIN

if (coalesce(nr_seq_preferencia_p::text, '') = '') then
	ds_Retorno_w := 'S';
else
	select	count(*)
	into STRICT	qt_regra_w
	from	agenda_regra_preferencia
	where	nr_seq_preferencia	= nr_seq_preferencia_p;

	if (qt_regra_w	> 0) then
		select	max(nr_sequencia)
		into STRICT	nr_seq_regra_w
		from	agenda_regra_preferencia
		where	nr_seq_preferencia	= nr_seq_preferencia_p
		and	cd_perfil		= cd_perfil_p
		and	cd_agenda		= cd_agenda_p;
		
		if (nr_seq_regra_w > 0) then
		
			select	count(*)
			into STRICT	qt_regra_usu_w
			from	agenda_regra_pref_usuario
			where	nr_seq_regra_pref	= nr_seq_regra_w;
			
			if (qt_regra_usu_w	> 0) then
				select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
				into STRICT	ds_retorno_w
				from	agenda_regra_pref_usuario
				where	nr_seq_regra_pref	= nr_seq_regra_w
				and	cd_pessoa_fisica	= cd_pessoa_fisica_p;
			else
				ds_retorno_w	:= 'S';
			end if;
		else
			ds_retorno_w	:= 'N';
		end if;
	else
		ds_Retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
