CREATE OR REPLACE FUNCTION obter_se_prematuro (qt_sem_ig_total_p bigint) RETURNS varchar AS $body$
DECLARE


ds_situacao_w              varchar(2) := 'N';

/*
Qt menor do que 27 semanas não é prematuro
Qt Maior ou igual a 27 semanas e menor ou igual a 37 semanas ele é prematuro
Qt Maior do que 37 semanas é Termo.
*/
BEGIN

if (coalesce(qt_sem_ig_total_p,0) >= 20 and coalesce(qt_sem_ig_total_p,0) <= 37) then

	ds_situacao_w := 'S';

end if;

if (coalesce(qt_sem_ig_total_p,0) > 37) then

	ds_situacao_w := 'T';

end if;

return	ds_situacao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
