CREATE OR REPLACE FUNCTION obter_se_prescrcpoe_vig_adep (dt_inicio_prescr_p timestamp, dt_validade_prescr_p timestamp, dt_inicio_param_p timestamp, dt_final_param_p timestamp, nr_seq_mat_cpoe_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ie_vigente_w	varchar(1) := 'N';
dt_fim_cpoe_w	timestamp;


BEGIN
if (dt_inicio_prescr_p IS NOT NULL AND dt_inicio_prescr_p::text <> '') and (dt_validade_prescr_p IS NOT NULL AND dt_validade_prescr_p::text <> '') and (dt_inicio_param_p IS NOT NULL AND dt_inicio_param_p::text <> '') and (dt_final_param_p IS NOT NULL AND dt_final_param_p::text <> '') then


	if (coalesce(nr_seq_mat_cpoe_p,0) > 0) then

		select	coalesce(dt_fim, dt_suspensao)
		into STRICT	dt_fim_cpoe_w
		from	cpoe_material
		where	nr_sequencia = nr_seq_mat_cpoe_p;


	    if (dt_fim_cpoe_w IS NOT NULL AND dt_fim_cpoe_w::text <> '') then
	       if (dt_fim_cpoe_w between dt_inicio_param_p and dt_final_param_p) then
				return 'S';
			else
				return 'N';
			end if;
		end if;
	end if;

    if (dt_inicio_param_p >= dt_inicio_prescr_p) and (dt_final_param_p <= dt_validade_prescr_p) then
		ie_vigente_w := 'S';

	elsif (dt_final_param_p >= dt_inicio_prescr_p) and (dt_final_param_p <= dt_validade_prescr_p) then
		ie_vigente_w := 'S';

	elsif (dt_inicio_param_p >= dt_inicio_prescr_p) and (dt_inicio_param_p <= dt_validade_prescr_p) then
		ie_vigente_w := 'S';

	elsif (dt_inicio_prescr_p >= dt_inicio_param_p) and (dt_validade_prescr_p <= dt_final_param_p) then
		ie_vigente_w := 'S';

	else
		ie_vigente_w := 'N';

	end if;

end if;

return ie_vigente_w;

end;
