SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_prescricao_aprovada ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aprov_w		varchar(1);
qt_exame_pendente_w	bigint;
qt_exame_aprov_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_exame_pendente_w
from	prescr_procedimento
where	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
and	ie_status_atend < 35
and	cd_motivo_baixa = 0
and	nr_prescricao = nr_prescricao_p;

ie_aprov_w	:= 'N';

if (qt_exame_pendente_w = 0) then

	select 	count(*)
	into STRICT	qt_exame_aprov_w
	from	prescr_procedimento
	where	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
	and	ie_status_atend >= 35
	and	nr_prescricao = nr_prescricao_p;

	if (qt_exame_aprov_w > 0)	then
		ie_aprov_w	:= 'S';
	end if;

end if;

return ie_aprov_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
