CREATE OR REPLACE FUNCTION obter_se_prescricao_cpoe ( nr_prescricao_p bigint, nr_prescricoes_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	prescr_medica
	where	cd_funcao_origem = 2314
	and		nr_prescricao	= nr_prescricao_p;
elsif (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	prescr_medica
	where	cd_funcao_origem = 2314
	and		nr_prescricao = obter_max_nrprescricao(nr_prescricoes_p);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
