CREATE OR REPLACE FUNCTION obter_se_prescricao_hemo ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_prescricoes_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(15);
lista_prescricao_w  lista_varchar_pck.tabela_varchar;
nr_prescricao_w  numeric(20);

BEGIN
ds_retorno_w := 'N';

if (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') then
	lista_prescricao_w := obter_lista_string2(nr_prescricoes_p,',');

	if (lista_prescricao_w.count > 0) then

		for i in lista_prescricao_w.first..lista_prescricao_w.last loop

			nr_prescricao_w := lista_prescricao_w(i);

			if (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

				select  coalesce(max(ie_hemodialise),'N')
				into STRICT 	ds_retorno_w
				from 	prescr_medica
				where 	nr_atendimento = nr_atendimento_p
				and 	nr_prescricao = nr_prescricao_w
				and 	((ie_hemodialise = 'E') or (ie_hemodialise = 'S'));

				if (ds_retorno_w = 'S') then
					return ds_retorno_w;
				end if;

			elsif (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

				select  coalesce(max(ie_hemodialise),'N')
				into STRICT	ds_retorno_w
				from 	prescr_medica
				where	cd_pessoa_fisica = cd_pessoa_fisica_p
				and		nr_prescricao = nr_prescricao_w
				and 	((ie_hemodialise = 'E') or (ie_hemodialise = 'S'));

				if (ds_retorno_w = 'S') then
					return ds_retorno_w;
				end if;
			end if;
		end loop;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
