CREATE OR REPLACE FUNCTION obter_se_prescricao_nut_dia (nr_prescricao_p bigint, nr_seq_servico_dia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(2);


BEGIN

ds_retorno_w := 'N';
if (nr_prescricao_p <> 0 ) then
	select 	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	nut_atend_serv_dia_rep
	where	nr_prescr_oral  = nr_prescricao_p
	and		nr_seq_serv_dia = nr_seq_servico_dia_p
	and		DT_ATUALIZACAO_NREC > clock_timestamp() - interval '7 days';

	if (ds_retorno_w = 'N') then

		select 	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	nut_atend_serv_dia_rep
		where	nr_prescr_enteral = nr_prescricao_p
		and		nr_seq_serv_dia = nr_seq_servico_dia_p
		and		DT_ATUALIZACAO_NREC > clock_timestamp() - interval '7 days';

		if (ds_retorno_w = 'N') then
			select 	coalesce(max('S'),'N')
			into STRICT	ds_retorno_w
			from	nut_atend_serv_dia_rep
			where	nr_prescr_leite_deriv = nr_prescricao_p
			and		nr_seq_serv_dia = nr_seq_servico_dia_p
			and		DT_ATUALIZACAO_NREC > clock_timestamp() - interval '7 days';

			if (ds_retorno_w = 'N') then
				select 	coalesce(max('S'),'N')
				into STRICT	ds_retorno_w
				from	nut_atend_serv_dia_rep
				where	nr_prescr_jejum = nr_prescricao_p
				and		nr_seq_serv_dia = nr_seq_servico_dia_p
				and		DT_ATUALIZACAO_NREC > clock_timestamp() - interval '7 days';

				if (ds_retorno_w = 'N') then
					select 	coalesce(max('S'),'N')
					into STRICT	ds_retorno_w
					from	nut_atend_serv_dia_rep
					where	nr_prescr_compl = nr_prescricao_p
					and		nr_seq_serv_dia = nr_seq_servico_dia_p
					and		DT_ATUALIZACAO_NREC > clock_timestamp() - interval '7 days';

					if (ds_retorno_w = 'N') then
						select 	coalesce(max('S'),'N')
						into STRICT	ds_retorno_w
						from	nut_atend_serv_dia_rep
						where	nr_prescr_npt_adulta = nr_prescricao_p
						and		nr_seq_serv_dia = nr_seq_servico_dia_p
						and		DT_ATUALIZACAO_NREC > clock_timestamp() - interval '7 days';

						if (ds_retorno_w = 'N') then
							select 	coalesce(max('S'),'N')
							into STRICT	ds_retorno_w
							from	nut_atend_serv_dia_rep
							where	nr_prescr_npt_neo = nr_prescricao_p
							and		nr_seq_serv_dia = nr_seq_servico_dia_p
							and		DT_ATUALIZACAO_NREC > clock_timestamp() - interval '7 days';

							if (ds_retorno_w = 'N') then
								select 	coalesce(max('S'),'N')
								into STRICT	ds_retorno_w
								from	nut_atend_serv_dia_rep
								where	nr_prescr_npt_ped = nr_prescricao_p
								and		nr_seq_serv_dia = nr_seq_servico_dia_p
								and		DT_ATUALIZACAO_NREC > clock_timestamp() - interval '7 days';
							end if;

						end if;

					end if;

				end if;

			end if;

		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
