CREATE OR REPLACE FUNCTION obter_se_prescricao_suspensa (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE
 ie_retorno_w	varchar(1) := 'N';

BEGIN

	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
		SELECT 	CASE WHEN COUNT(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_retorno_w
		FROM   	prescr_medica
		WHERE  	nr_prescricao = nr_prescricao_p
		AND    	coalesce(dt_suspensao::text, '') = '';

		if (ie_retorno_w = 'N') then
			select CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_retorno_w
			from prescr_procedimento
			where nr_prescricao = nr_prescricao_p
			and (NR_SEQ_SOLIC_SANGUE IS NOT NULL AND NR_SEQ_SOLIC_SANGUE::text <> '')
			and (dt_suspensao IS NOT NULL AND dt_suspensao::text <> '');
		end if;
	end if;
return	ie_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
