CREATE OR REPLACE FUNCTION obter_se_prescrmat_baixa (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
nr_prescricao_w		bigint;
qt_material_prescr_w	integer;
qt_mat_prescr_baixa_w	integer;


BEGIN

select	max(nr_prescricao)
into STRICT	nr_prescricao_w
from	cirurgia
where	nr_cirurgia	= nr_cirurgia_p;

select	count(*)
into STRICT	qt_material_prescr_w
from	prescr_material
where	nr_prescricao	= nr_prescricao_w;

select	count(*)
into STRICT	qt_mat_prescr_baixa_w
from	prescr_material
where	nr_prescricao	= nr_prescricao_w
and	(dt_baixa IS NOT NULL AND dt_baixa::text <> '')
and	(cd_motivo_baixa IS NOT NULL AND cd_motivo_baixa::text <> '');

if (qt_material_prescr_w > 0) and (qt_material_prescr_w	= qt_mat_prescr_baixa_w) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
