CREATE OR REPLACE FUNCTION obter_se_prescrs_pend_atend ( nr_atendimento_p atendimento_paciente.nr_atendimento%type ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	char(1)	:= 'N';


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	prescr_medica z
	where	z.nr_atendimento = nr_atendimento_p
	and		coalesce(z.ie_emergencia,'N') = 'S'
	and		coalesce(z.dt_liberacao::text, '') = ''
	and		coalesce(z.dt_suspensao::text, '') = ''
	and		coalesce(z.dt_liberacao_medico::text, '') = ''  LIMIT 1;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
