CREATE OR REPLACE FUNCTION obter_se_prescr_acm_sn (nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



count_w	bigint;
ds_retorno_w	varchar(01);



BEGIN

select	count(*)
into STRICT	count_w
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and	((coalesce(ie_acm,'N') = 'S' and ie_opcao_p = 'ACM')
	or (coalesce(ie_se_necessario,'N') = 'S' and ie_opcao_p = 'SN'));

if (coalesce(count_w,0) > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
