CREATE OR REPLACE FUNCTION obter_se_prescr_aprovado ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aprov_w		varchar(1);
qt_exame_pendente_w	bigint;
qt_exame_aprov_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_exame_pendente_w
from	prescr_procedimento a,
	prescr_medica b
where	a.nr_prescricao = b.nr_prescricao
and	b.nr_prescricao = nr_prescricao_p
and	(a.nr_seq_exame IS NOT NULL AND a.nr_seq_exame::text <> '')
and	a.ie_suspenso <> 'S'
and	not exists (	SELECT 1
			from 	exame_lab_result_item y,
				exame_lab_resultado x
		    where y.nr_seq_resultado = x.nr_seq_resultado
		    and   y.nr_seq_prescr    = a.nr_sequencia
		    and   x.nr_prescricao    = b.nr_prescricao
		    and   (y.dt_aprovacao IS NOT NULL AND y.dt_aprovacao::text <> ''));

ie_aprov_w	:= 'N';

if (qt_exame_pendente_w = 0) then
	ie_aprov_w	:= 'S';
end if;

return ie_aprov_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_prescr_aprovado ( nr_prescricao_p bigint) FROM PUBLIC;

