SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_prescr_comunic_cih (nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w			prescr_medica.nr_prescricao%type;
nr_sequencia_w			prescr_material.nr_sequencia%type;
cd_material_w			prescr_material.cd_material%type;
nr_dia_util_w			prescr_material.nr_dia_util%type;
cd_estabelecimento_w	bigint;
ie_atb_pessoa_w			varchar(1);
dt_prescricao_w			prescr_medica.dt_prescricao%type;
dt_anterior_w			prescr_medica.dt_prescricao%type;
nr_atendimento_w		prescr_medica.nr_atendimento%type;
cd_pessoa_fisica_w		prescr_medica.cd_pessoa_fisica%type;
ie_dias_util_medic_w	varchar(1);
ie_concordo_w			varchar(1) := 'N';



BEGIN

cd_estabelecimento_w	:= coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);

select	max(cd_material),
	max(nr_dia_util)
into STRICT 	cd_material_w,
	nr_dia_util_w
from  	prescr_material
where 	nr_sequencia 	= nr_seq_material_p
and	nr_prescricao 		= nr_prescricao_p;

select 	max(dt_prescricao),
	max(nr_atendimento),
	max(cd_pessoa_fisica)
into STRICT   dt_prescricao_w,
	nr_atendimento_w,
	cd_pessoa_fisica_w
from   	prescr_medica
where  	nr_prescricao = nr_prescricao_p;

select	coalesce(max(ie_atb_pessoa),'N')
into STRICT	ie_atb_pessoa_w
from	parametro_medico
where	cd_estabelecimento	= cd_estabelecimento_w;

select	coalesce(max(ie_dias_util_medic),'N')
into STRICT	ie_dias_util_medic_w
from	material
where	cd_material	= cd_material_w;

dt_anterior_w	:= trunc(dt_prescricao_w,'dd') - nr_dia_util_w;

if (coalesce(ie_atb_pessoa_w,'N') = 'N') then
	select	min(a.nr_prescricao),
		min(a.nr_sequencia)
	into STRICT  nr_prescricao_w,
		nr_sequencia_w
	from 	prescr_material a,
		prescr_medica b
	where  a.nr_prescricao	= b.nr_prescricao
	and	a.nr_prescricao <> nr_prescricao_p
	and	((b.dt_inicio_prescr between dt_anterior_w and dt_prescricao_w) or (b.dt_prescricao between dt_anterior_w and dt_prescricao_w))
	and	b.nr_atendimento	     = nr_atendimento_w
	and	a.cd_material  		     = cd_material_w
	and (a.nr_dia_util IS NOT NULL AND a.nr_dia_util::text <> '')
	and	((ie_dias_util_medic_w = 'O' AND a.nr_dia_util = 0) or
		 (ie_dias_util_medic_w = 'S' AND a.nr_dia_util = 1));
elsif (coalesce(ie_atb_pessoa_w,'N') = 'S') then
	select	min(a.nr_prescricao),
		min(a.nr_sequencia)
	into STRICT  nr_prescricao_w,
		nr_sequencia_w
	from 	prescr_material a,
		prescr_medica b
	where 	a.nr_prescricao  = b.nr_prescricao
	and	b.nr_prescricao	  <> nr_prescricao_p
	and	((b.dt_inicio_prescr between dt_anterior_w and dt_prescricao_w) or (b.dt_prescricao between dt_anterior_w and dt_prescricao_w))
	and b.cd_pessoa_fisica	     = cd_pessoa_fisica_w
	and	a.cd_material 	     = cd_material_w
	and 	(a.nr_dia_util IS NOT NULL AND a.nr_dia_util::text <> '')
	and	((ie_dias_util_medic_w = 'O' AND a.nr_dia_util = 0) or
		 (ie_dias_util_medic_w = 'S' AND a.nr_dia_util = 1));
end if;

select coalesce(max(ie_concordo),'N')
into STRICT  ie_concordo_w
from  prescr_mat_comunic_cih
where nr_prescricao     = nr_prescricao_w
and	  nr_seq_prescricao = nr_sequencia_w;


return ie_concordo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
