CREATE OR REPLACE FUNCTION obter_se_prescr_conformidade (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_prescr_erro_w	varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_prescr_erro_w
	from	prescr_material a,
		prescr_medica_erro b
	where	a.nr_prescricao	= b.nr_prescricao
	and	b.nr_seq_medic	= a.nr_sequencia
	and	a.nr_prescricao = nr_prescricao_p
	and	a.ie_agrupador	= 1;
end if;

return coalesce(ie_prescr_erro_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
