CREATE OR REPLACE FUNCTION obter_se_prescr_confor_ged (nr_seq_processo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_prescr_erro_w	varchar(1);


BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_prescr_erro_w
	FROM    prescr_mat_hor c,
		prescr_material b,
		prescr_medica_erro a
	WHERE   c.nr_prescricao  = b.nr_prescricao
	AND 	c.NR_SEQ_MATERIAL = b.nr_sequencia
	AND 	a.nr_seq_medic = b.nr_sequencia
	AND	a.nr_prescricao  = b.nr_prescricao
	AND    	(a.nr_seq_medic IS NOT NULL AND a.nr_seq_medic::text <> '')
	AND     c.nr_seq_processo = nr_seq_processo_p
	and	a.ie_agrupador	= 1;
end if;

return coalesce(ie_prescr_erro_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
