SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_prescr_conta ( nr_interno_conta_p bigint, ds_prescricoes_p text) RETURNS varchar AS $body$
DECLARE


qt_proc_w	bigint;
qt_mat_w	bigint;
ds_retorno_w	varchar(255) := 'N';


BEGIN

if (coalesce(nr_interno_conta_p,0) <> 0) and (ds_prescricoes_p IS NOT NULL AND ds_prescricoes_p::text <> '') then

	select	count(*)
	into STRICT	qt_proc_w
	from	procedimento_paciente a
	where	a.nr_interno_conta = nr_interno_conta_p
	and	obter_se_contido(a.nr_prescricao, ds_prescricoes_p) = 'S'
	and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

	if (qt_proc_w > 0) then
		ds_retorno_w	:= 'S';
	else

		select	count(*)
		into STRICT	qt_mat_w
		from	material_atend_paciente a
		where	a.nr_interno_conta = nr_interno_conta_p
		and	obter_se_contido(a.nr_prescricao, ds_prescricoes_p) = 'S'
		and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

		if (qt_mat_w > 0) then
			ds_retorno_w	:= 'S';
		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
