CREATE OR REPLACE FUNCTION obter_se_prescr_cpoe (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cpoe_w	varchar(1);


BEGIN

select	coalesce(max(ie_cpoe),'N')
into STRICT	ie_cpoe_w
from	(
	SELECT	'S' ie_cpoe
	from	prescr_dieta
	where	nr_prescricao	= nr_prescricao_p
	and	(nr_seq_dieta_cpoe IS NOT NULL AND nr_seq_dieta_cpoe::text <> '')
	
union

	SELECT	'S' ie_cpoe
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	((nr_seq_dieta_cpoe IS NOT NULL AND nr_seq_dieta_cpoe::text <> '') or (nr_Seq_mat_cpoe IS NOT NULL AND nr_Seq_mat_cpoe::text <> ''))
	
union

	select	'S' ie_cpoe
	from	rep_jejum
	where	nr_prescricao	= nr_prescricao_p
	and	(nr_seq_dieta_cpoe IS NOT NULL AND nr_seq_dieta_cpoe::text <> '')
	
union

	select	'S' ie_cpoe
	from	prescr_leite_deriv
	where	nr_prescricao	= nr_prescricao_p
	and	(nr_seq_dieta_cpoe IS NOT NULL AND nr_seq_dieta_cpoe::text <> '')
	
union

	select	'S' ie_cpoe
	from	nut_pac
	where	nr_prescricao = nr_prescricao_p
	and	(nr_seq_npt_cpoe IS NOT NULL AND nr_seq_npt_cpoe::text <> '')
	
union

	select	'S' ie_cpoe
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and	(nr_seq_proc_cpoe IS NOT NULL AND nr_seq_proc_cpoe::text <> '')
	
union

	select	'S' ie_cpoe
	from	prescr_gasoterapia
	where	nr_prescricao = nr_prescricao_p
	and	(nr_seq_gas_cpoe IS NOT NULL AND nr_seq_gas_cpoe::text <> '')
	
union

	select	'S' ie_cpoe
	from	prescr_recomendacao
	where	nr_prescricao	= nr_prescricao_p
	and	(nr_Seq_rec_cpoe IS NOT NULL AND nr_Seq_rec_cpoe::text <> '')
	
union

	select	'S' ie_cpoe
	from	prescr_solic_bco_Sangue
	where	nr_prescricao = nr_prescricao_p
	and	(nr_seq_hemo_cpoe IS NOT NULL AND nr_seq_hemo_cpoe::text <> '')
	
union

	select	'S' ie_cpoe
	from	prescr_solucao
	where	nr_prescricao = nr_prescricao_p
	and	(nr_seq_dialise_cpoe IS NOT NULL AND nr_seq_dialise_cpoe::text <> '')) alias14;

return	ie_cpoe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
