CREATE OR REPLACE FUNCTION obter_se_prescr_curta_duracao ( nr_prescricao_p bigint, hr_final_prescr_p text, cd_perfil_p bigint, cd_medico_p text, cd_especialidade_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w		char(1)		:= 'N';
hr_final_prescr_w	timestamp;
cd_perfil_w			perfil.cd_perfil%type;
cd_especialidade_w	especialidade_medica.cd_especialidade%type;


BEGIN 
 
if (coalesce(nr_prescricao_p,0) = 0) and (hr_final_prescr_p IS NOT NULL AND hr_final_prescr_p::text <> '') then 
	select	coalesce(max('S'), 'N') 
	into STRICT	ie_retorno_w 
	from	regra_plt_curta_duracao LIMIT 1;
	 
	if (ie_retorno_w = 'S') then 
		cd_perfil_w			:= coalesce(cd_perfil_p,0);
		cd_especialidade_w	:= coalesce(cd_especialidade_p, coalesce(obter_especialidade_medico2(cd_medico_p),0));
 
		select	coalesce(max('S'), 'N') 
		into STRICT	ie_retorno_w 
		from	regra_plt_curta_duracao 
		where	coalesce(cd_especialidade, cd_especialidade_w) = cd_especialidade_w 
		and		coalesce(cd_perfil, cd_perfil_w) = cd_perfil_w;
	 
		if (ie_retorno_w = 'S') then 
			hr_final_prescr_w := to_date(hr_final_prescr_p,'dd/mm/yyyy hh24:mi:ss');
			 
			select	coalesce(max('S'), 'N') 
			into STRICT 	ie_retorno_w 
			from	prescr_medica where		nr_prescricao = nr_prescricao_p 
			and	  	to_char(dt_validade_prescr, 'hh24:mi:ss') <> to_char(hr_final_prescr_w,'hh24:mi:ss')  
			and	  	coalesce(dt_liberacao_medico::text, '') = '' 
			and		coalesce(dt_liberacao::text, '') = '' LIMIT 1;
		end if;
	end if;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
