CREATE OR REPLACE FUNCTION obter_se_prescr_dieta (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_dieta_w		varchar(1);


BEGIN

select	CASE WHEN sum(qt)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_dieta_w
from (SELECT	count(nr_sequencia) qt
		from	prescr_dieta
		where	nr_prescricao	= nr_prescricao_p
		
union all

		SELECT	count(nr_sequencia) qt
		from	rep_jejum
		where	nr_prescricao	= nr_prescricao_p
		
union all

		select	count(nr_sequencia) qt
		from	prescr_leite_deriv
		where	nr_prescricao	= nr_prescricao_p) alias4;

return	ie_dieta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
