SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_prescr_hor_passado ( nr_prescricao_p bigint, qt_hor_passada_p bigint) RETURNS varchar AS $body$
DECLARE


ie_hor_passado_w		varchar(1) := 'N';
ie_prescr_emergencia_w		varchar(1);
dt_limite_w			timestamp;


BEGIN

select	coalesce(max(ie_prescr_emergencia),'N')
into STRICT	ie_prescr_emergencia_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

if (ie_prescr_emergencia_w	= 'N') and (qt_hor_passada_p	> 0) then

	dt_limite_w	:= clock_timestamp() - (coalesce(qt_hor_passada_p,0)/24);

	select	CASE WHEN sum(qt_hor_passado)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_hor_passado_w
	from	(
		SELECT	count(*) qt_hor_passado
		from	prescr_mat_hor a,
			prescr_material b
		where	a.nr_prescricao	= nr_prescricao_p
		and	a.nr_prescricao	= b.nr_prescricao
		and	a.nr_seq_material = b.nr_sequencia
		and	a.dt_horario	< dt_limite_w
		and	((coalesce(a.ie_horario_especial,'N') 	<> 'S') or (b.ie_acm = 'S'))
		and	coalesce(a.ie_urgente, 'N')		<> 'S'
		and	coalesce(a.ie_situacao,'A')		<> 'I'
		
union all

			SELECT	count(*) qt_hor_passado
		from	prescr_proc_hor
		where	nr_prescricao	= nr_prescricao_p
		and	dt_horario	< dt_limite_w
		and	coalesce(ie_horario_especial,'N') 	<> 'S'
		and	coalesce(ie_urgente, 'N')		<> 'S'
		and	coalesce(ie_situacao,'A')		<> 'I'
		
union all

			select	count(*) qt_hor_passado
		from	prescr_rec_hor
		where	nr_prescricao	= nr_prescricao_p
		and	dt_horario	< dt_limite_w
		and	coalesce(ie_horario_especial,'N') 	<> 'S'
		and	coalesce(ie_situacao,'A')		<> 'I'
		
union all

			select	count(*) qt_hor_passado
		from	prescr_dieta_hor
		where	nr_prescricao	= nr_prescricao_p
		and	dt_horario	< dt_limite_w
		and	coalesce(ie_situacao,'A')		<> 'I') alias17;

end if;

return	ie_hor_passado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
