CREATE OR REPLACE FUNCTION obter_se_prescr_hor_urg (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_urgente_w	bigint;
ds_retorno_w	varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_urgente_w
from	prescr_mat_hor
where	nr_prescricao	= nr_prescricao_p
and	ie_urgente	= 'S'
and	coalesce(dt_emissao_farmacia::text, '') = ''
and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

if (qt_urgente_w > 0) then
	ds_retorno_w	:= 'S';
end if;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
