CREATE OR REPLACE FUNCTION obter_se_prescr_hor (nr_prescricao_p bigint, nr_seq_turno_p bigint) RETURNS varchar AS $body$
DECLARE



ie_retorno_w		varchar(01)	:= 'N';
nr_sequencia_w		bigint;

c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	prescr_material a
	where	nr_prescricao	= nr_prescricao_p;


BEGIN

open	c01;
loop
fetch	c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ie_retorno_w	= 'N') then
		select	coalesce(max('S'), 'N')
		into STRICT	ie_retorno_w
		from	prescr_mat_hor
		where	nr_prescricao		= nr_prescricao_p
		and	nr_seq_material		= nr_sequencia_w
		and	nr_seq_turno		= nr_seq_turno_p
		and	coalesce(dt_disp_farmacia::text, '') = ''
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';
	end if;

	end;
end loop;
close c01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
