CREATE OR REPLACE FUNCTION obter_se_prescr_imp_parcial ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



qt_impressao_w		integer;
ie_imp_parcial_w		varchar(1) := 'N';

/*Identifica se a prescrição já teve impressão em algum local de estoque  */

BEGIN

select	count(*)
into STRICT	qt_impressao_w
from	prescr_medica a,
	prescr_emissao_local b
where	coalesce(a.dt_emissao_farmacia::text, '') = ''
and	a.nr_prescricao	= b.nr_prescricao
and	a.nr_prescricao	= nr_prescricao_p;
if (qt_impressao_w > 0) then
	ie_imp_parcial_w	:= 'S';
end if;

return ie_imp_parcial_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
