CREATE OR REPLACE FUNCTION obter_se_prescr_item_suspensos (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

qt_susp_w		bigint;
ds_retorno_w	varchar(1);

BEGIN
select 	sum(qt_susp)
into STRICT		qt_susp_w
from (	SELECT	count(*) qt_susp
			from		prescr_material
			where		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			and		nr_prescricao	= nr_prescricao_p
			
union

			SELECT	count(*) qt_susp
			from		prescr_procedimento
			where	coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			and		nr_prescricao	= nr_prescricao_p
			
union

			select	count(*) qt_susp
			from		prescr_dieta
			where	coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			and		nr_prescricao	= nr_prescricao_p
			
union

			select	count(*) qt_susp
			from		rep_jejum
			where	nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'S'
			
union

			select	count(*) qt_susp
			from		nut_pac
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			
union

			select	count(*) qt_susp
			from		prescr_solucao
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			
union

			select	count(*) qt_susp
			from		prescr_gasoterapia
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			
union

			select	count(*) qt_susp
			from		prescr_recomendacao
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')) alias24;


ds_retorno_w := 'N';
if (qt_susp_w > 0) then
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
