CREATE OR REPLACE FUNCTION obter_se_prescr_item_susp (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

qt_susp_w		bigint;
ds_retorno_w	varchar(1);

BEGIN
select 	sum(qt_susp)
into STRICT		qt_susp_w
from (	SELECT	count(*) qt_susp
			from		material a,
					prescr_material b
			where	a.cd_material	= b.cd_material
			and		b.ie_suspenso	= 'S'
			and		b.ie_agrupador in (1,4,8,12)
			and		b.nr_prescricao	= nr_prescricao_p
			
union

			SELECT	count(*) qt_susp
			from		procedimento a,
					prescr_procedimento b
			where	a.cd_procedimento	= b.cd_procedimento
			and		a.ie_origem_proced	= b.ie_origem_proced
			and		b.ie_suspenso	= 'S'
			and		b.nr_prescricao	= nr_prescricao_p) alias4;
ds_retorno_w := 'N';
if (qt_susp_w > 0) then
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
