SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_prescr_item_urgente (nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_mat_urgente_w	integer;
qt_proc_urgente_w	integer;
qt_urgente_w		integer	:= 0;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select	sum(CASE WHEN m.ie_urgencia='S' THEN 1  ELSE 0 END ) nr_itens_urgentes
	into STRICT	qt_mat_urgente_w
	from	prescr_material m
	where	m.nr_prescricao	= nr_prescricao_p;

	select	sum(CASE WHEN p.ie_urgencia='S' THEN 1  ELSE 0 END ) nr_itens_urgentes
	into STRICT	qt_proc_urgente_w
	from	prescr_procedimento p
	where	p.nr_prescricao	= nr_prescricao_p;
end if;

qt_urgente_w	:= (coalesce(qt_mat_urgente_w,0) + coalesce(qt_proc_urgente_w,0));

RETURN	qt_urgente_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
