CREATE OR REPLACE FUNCTION obter_se_prescr_liberada_web ( nr_prescricao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE



ie_liberada_w   	varchar(1);				
qt_liberada_w	bigint;
qt_exames_w	bigint;

				

BEGIN

ie_liberada_w 	:= 'N';

select count(*)
into STRICT     qt_liberada_w	
from    prescr_procedimento
where obter_status_result_exame_site(nr_prescricao,nr_sequencia,nm_usuario_p, cd_estabelecimento_p) = 'L'
and    obter_tipo_baixa_prescr(cd_motivo_baixa) = 'N'
and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
and    coalesce(ie_suspenso,'N') = 'N'
and    nr_prescricao = nr_prescricao_p;

select   count(*)
into STRICT      qt_exames_w
from     prescr_procedimento
where  obter_tipo_baixa_prescr(cd_motivo_baixa) = 'N'
and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
and     coalesce(ie_suspenso,'N') = 'N'
and    nr_prescricao = nr_prescricao_p;

if (qt_liberada_w = qt_exames_w) then
	ie_liberada_w := 'S';
end if;

return	ie_liberada_w;

end;
$body$
