CREATE OR REPLACE FUNCTION obter_se_prescr_liberada (nr_seq_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

ie_exige_lib_farma_w varchar(1);
ds_retorno_w varchar(1);

BEGIN

ie_exige_lib_farma_w := Obter_Param_Usuario(3130, 429, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_exige_lib_farma_w);

ds_retorno_w := 'S';

if ((nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and coalesce(ie_exige_lib_farma_w, 'N') <> 'N') then

    select coalesce(max('S'), 'N')
    into STRICT   ds_retorno_w
    from   prescr_medica p,
           paciente_atendimento a
    where  p.nr_prescricao = a.nr_prescricao
    and    a.nr_seq_atendimento = nr_seq_atendimento_p
    and    (p.dt_liberacao_farmacia IS NOT NULL AND p.dt_liberacao_farmacia::text <> ''); -- must have dt_liberacao_farmacia
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
