CREATE OR REPLACE FUNCTION obter_se_prescr_lib_cpoe_farm (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_liberado_w		varchar(1);

c01 CURSOR FOR
SELECT	dt_liberacao_farm
from	cpoe_material
where	nr_sequencia in (
	SELECT	nr_seq_mat_cpoe
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and 	coalesce(dt_suspensao::text, '') = ''
)
and (coalesce(coalesce(dt_lib_suspensao, dt_suspensao)::text, '') = '' or coalesce(dt_lib_suspensao, dt_suspensao) > clock_timestamp());
		
BEGIN
	begin
		for r_c01_w in c01
		loop
			if (coalesce(ie_liberado_w::text, '') = '') then
				if (r_c01_w.dt_liberacao_farm IS NOT NULL AND r_c01_w.dt_liberacao_farm::text <> '') then
					ie_liberado_w := 'S';
				else
					ie_liberado_w := 'N';
					exit;
				end if;
			else
				if (coalesce(r_c01_w.dt_liberacao_farm::text, '') = '') then
					ie_liberado_w := 'N';
					exit;
				end if;
			end if;
		end loop;
	exception
		when no_data_found then
			ie_liberado_w := null;
	end;

	return	ie_liberado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
