CREATE OR REPLACE FUNCTION obter_se_prescr_mat_gpt ( nr_prescricao_p prescr_medica.nr_prescricao%type, cd_material_p prescr_material.cd_material%type, ds_matmeds_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(1);
			

BEGIN

if (coalesce(ds_matmeds_p::text, '') = '') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	material b,
			prescr_material a
	where	a.cd_material = b.cd_material
	and		a.nr_prescricao = nr_prescricao_p
	and		((b.cd_material = cd_material_p) or (b.cd_material_estoque = cd_material_p));
else
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	material b,
			prescr_material a
	where	a.cd_material = b.cd_material
	and		a.nr_prescricao = nr_prescricao_p
	and		((b.cd_material in (SELECT x.cd_registro
								from table(lista_pck.obter_lista_char(ds_matmeds_p,',')) x)) 
				or (b.cd_material_estoque in (select x.cd_registro 
											from table(lista_pck.obter_lista_char(ds_matmeds_p,',')) x)));
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
