CREATE OR REPLACE FUNCTION obter_se_prescr_nutri ( nr_prescricao_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_nutricao_w	varchar(1) := 'N';


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_nutricao_w
from	prescr_dieta a,
	prescr_medica b
where	a.nr_prescricao = b.nr_prescricao
and     (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
and	a.nr_prescricao = nr_prescricao_p;

if (ie_nutricao_w = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_nutricao_w
	from	prescr_material a,
		prescr_medica b
	where	a.nr_prescricao = b.nr_prescricao
	and     (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and	((ie_agrupador = 12) or (ie_agrupador = 8))
	and	a.nr_prescricao = nr_prescricao_p;
end if;

return	ie_nutricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
