CREATE OR REPLACE FUNCTION obter_se_prescr_nut ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint	:= 0;
ie_agrupador_w		smallint;

C01 CURSOR FOR
	SELECT	count(*),
			ie_agrupador
	from	prescr_material
	where	coalesce(ie_suspenso, 'N')	<> 'S'
	and		ie_agrupador in (1,2,6,8,10,11,12,16,17)
	and		nr_prescricao	= nr_prescricao_p
	group by ie_agrupador
	order by ie_agrupador;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	prescr_solucao
where	coalesce(ie_suspenso, 'N')	<> 'S'
and		nr_prescricao	= nr_prescricao_p;

if (qt_registro_w > 0) then
	return 'N';
end if;

select	count(*)
into STRICT	qt_registro_w
from	prescr_recomendacao
where	coalesce(ie_suspenso, 'N')	<> 'S'
and		nr_prescricao	= nr_prescricao_p;

if (qt_registro_w > 0) then
	return 'N';
end if;

select	count(*)
into STRICT	qt_registro_w
from	prescr_solic_bco_sangue
where	nr_prescricao	= nr_prescricao_p;

if (qt_registro_w > 0) then
	return 'N';
end if;

select	count(*)
into STRICT	qt_registro_w
from	prescr_gasoterapia
where	coalesce(ie_suspenso,'N') <> 'S'
and		nr_prescricao	= nr_prescricao_p;

if (qt_registro_w > 0) then
	return 'N';
end if;

open C01;
loop
fetch C01 into
	qt_registro_w,
	ie_agrupador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_agrupador_w = 1) then
		return 'N';
	elsif (ie_agrupador_w = 2) then
		return 'N';
	elsif (ie_agrupador_w in (6,8,10,11,12,16,17)) then
		return 'S';
	end if;
	end;
end loop;
close C01;

qt_registro_w := 0;

select	count(*)
into STRICT	qt_registro_w
from	prescr_dieta
where	coalesce(ie_suspenso, 'N')	<> 'S'
and		nr_prescricao	= nr_prescricao_p;

if (qt_registro_w > 0) then
	return 'S';
end if;

select	count(*)
into STRICT	qt_registro_w
from	rep_jejum
where	coalesce(ie_suspenso, 'N')	<> 'S'
and		nr_prescricao	= nr_prescricao_p;

if (qt_registro_w > 0) then
	return 'S';
end if;

select	count(*)
into STRICT	qt_registro_w
from	nut_paciente
where	coalesce(ie_suspenso, 'N')	<> 'S'
and		nr_prescricao	= nr_prescricao_p;

if (qt_registro_w > 0) then
	return 'S';
end if;

select	count(*)
into STRICT	qt_registro_w
from	nut_pac
where	coalesce(ie_suspenso, 'N')	<> 'S'
and		nr_prescricao	= nr_prescricao_p;

if (qt_registro_w > 0) then
	return 'S';
end if;

return 'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
