CREATE OR REPLACE FUNCTION obter_se_prescr_pac_jejum (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_jejum_w		varchar(1) := 'N';
cont_w			bigint;
nr_atendimento_w	bigint;
dt_inicio_prescr_w	timestamp;
dt_validade_prescr_w	timestamp;


BEGIN

select max(dt_inicio_prescr),
       max(dt_validade_prescr),
       max(nr_atendimento)
into STRICT   dt_inicio_prescr_w,
       dt_validade_prescr_w,
       nr_atendimento_w
from   prescr_medica
where  nr_prescricao = nr_prescricao_p;


select	count(*)
into STRICT	cont_w
from	prescr_medica b,
	rep_jejum a
where	b.nr_atendimento	= nr_atendimento_w
and	b.nr_prescricao		= a.nr_prescricao
and	coalesce(a.IE_SUSPENSO,'N') = 'N'
and	coalesce(b.dt_suspensao::text, '') = ''
and	obter_se_prescr_vig_jejum(coalesce(a.dt_inicio, b.dt_inicio_prescr),coalesce(a.dt_fim, b.dt_validade_prescr),dt_inicio_prescr_w,dt_validade_prescr_w) = 'S'
and	(coalesce(b.dt_liberacao, b.dt_liberacao_medico) IS NOT NULL AND (coalesce(b.dt_liberacao, b.dt_liberacao_medico))::text <> '');

if (cont_w > 0) then
	ie_jejum_w	:= 'S';
end if;

return	ie_jejum_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
