CREATE OR REPLACE FUNCTION obter_se_prescr_pend_data ( nr_atendimento_p bigint, dt_inicio_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_existe_w			varchar(1) := 'N';
ie_prescr_pend_w			varchar(1);

C01 CURSOR FOR
SELECT	obter_se_prescr_pend_serv(nr_sequencia)
from	nut_atend_serv_dia
where	nr_atendimento = nr_atendimento_p
and	dt_servico between dt_inicio_p and dt_final_p;


BEGIN

open c01;
		loop
		fetch c01 into ie_prescr_pend_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			if (ie_prescr_pend_w = 'S') then
				ie_existe_w := ie_prescr_pend_w;
			end if;
			end;
		end loop;
		close c01;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
