CREATE OR REPLACE FUNCTION obter_se_prescr_plano (nr_prescricao_p bigint, dt_plano_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
from	prescr_medica
where	nr_prescricao	=	nr_prescricao_p
and	(dt_plano_p IS NOT NULL AND dt_plano_p::text <> '')
and	((dt_rep_pt	=	dt_plano_p ) or (dt_rep_pt2	=	dt_plano_p));


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
