CREATE OR REPLACE FUNCTION obter_se_prescr_prev (nr_atendimento_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
cont_w		smallint;


BEGIN

select	count(*)
into STRICT	cont_w
from	prescr_procedimento b,
	prescr_medica a
where	a.nr_prescricao		= b.nr_prescricao
and	coalesce(b.dt_prev_execucao, dt_referencia_p - 1)	>= dt_referencia_p
and	a.nr_atendimento	= nr_atendimento_p
and	((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> ''))
and	coalesce(a.dt_suspensao::text, '') = ''
and	coalesce(b.ie_suspenso,'N')	<> 'S'
and	b.cd_motivo_baixa	= 0;

ds_retorno_w	:= 'N';
if (cont_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
