CREATE OR REPLACE FUNCTION obter_se_prescr_proc_recoleta (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(1);
qt_recoleta_w	bigint;


BEGIN

SELECT 	COUNT(*)
INTO STRICT	qt_recoleta_w
FROM	prescr_procedimento
WHERE	nr_prescricao = nr_prescricao_p
AND	(nr_seq_recoleta IS NOT NULL AND nr_seq_recoleta::text <> '');

IF (qt_recoleta_w > 0) THEN
	ds_retorno_w := 'S';
ELSE
	ds_retorno_w := 'N';
END IF;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
